'use strict';

const Hapi = require('@hapi/hapi');
const serverless = require('serverless-http');

const server = Hapi.server({
  port: process.env.PORT || 3000,
  routes: {
    cors: true
  }
});

server.route({
  method: 'GET',
  path: '/',
  handler: (request, h) => {
    return 'Server is running!';
  }
});

module.exports.handler = serverless(server);